mapper.select:
		SELECT uri.id as userroleId,uri.userId ,uri.roleId,role.roleName FROM userrole_info uri
 		LEFT JOIN role_info role ON uri.roleId = role.id AND role.RoleName = #{roleName} 

 		WHERE uri.userId=#{userId} and deleteStatus=1

		SELECT rmi.id AS rolemenuId,rmi.menuId ,rmi.roleId,role.roleName FROM rolemenu_info rmi
 		LEFT JOIN role_info role ON rmi.roleId = role.id AND role.RoleName = #{roleName} 

 		WHERE rmi.menuId=#{menuId} and deleteStatus=1
